#!/usr/bin/bash 
## TRILL: Visualize the embeddings in 2d using the visualize functionality
trill --outdir Results --n_workers 40 vis 0 visualize Results/crystallization_esm2_t6_8M_AVG.csv --method UMAP --key Data/Crystallization/train_key.csv
trill --outdir Results --n_workers 40 vis 0 visualize Results/crystallization_esm2_t12_35M_AVG.csv --method UMAP --key Data/Crystallization/train_key.csv
trill --outdir Results --n_workers 40 vis 0 visualize Results/crystallization_esm2_t30_150M_AVG.csv --method UMAP --key Data/Crystallization/train_key.csv
trill --outdir Results --n_workers 40 vis 0 visualize Results/crystallization_esm2_t33_650M_AVG.csv --method UMAP --key Data/Crystallization/train_key.csv
trill --outdir Results --n_workers 40 vis 0 visualize Results/crystallization_esm2_t36_3B_AVG.csv --method UMAP --key Data/Crystallization/train_key.csv
trill --outdir Results --n_workers 40 vis 0 visualize Results/crystallization_ProtT5-XL_AVG.csv --method UMAP --key Data/Crystallization/train_key.csv
trill --outdir Results --n_workers 40 vis 0 visualize Results/crystallization_ProstT5_AVG.csv --method UMAP --key Data/Crystallization/train_key.csv
trill --outdir Results --n_workers 40 vis 0 visualize Results/crystallization_Ankh_AVG.csv --method UMAP --key Data/Crystallization/train_key.csv
trill --outdir Results --n_workers 40 vis 0 visualize Results/crystallization_Ankh-Large_AVG.csv --method UMAP --key Data/Crystallization/train_key.csv
mv *.csv Results/
## TRILL: Perform XGBoost classification on the embeddings generated by PLMs for crystallizable vs non-crystallizable
trill --outdir XGBoost --n_workers 40 esm2_t6_8M 1 classify XGBoost Data/Crystallization/crystallization_train.fasta --emb_model esm2_t6_8M --preComputed_Embs Results/crystallization_esm2_t6_8M_AVG.csv --train_split 0.75 --sweep --sweep_cv 10 --n_estimators 200 --f1_avg_method weighted --key Data/Crystallization/train_key.csv
